INSERT INTO `events` VALUES (13,13,'az','Rəsmi IELTS İmtahan tarixləri','<p><span style=\"font-size:14px\">Barattson IDP: IELTS Australia tərəfindən akkreditə olunmuş, AZ018 n&ouml;mrəli lisenziyalı test mərkəzidir.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>IELTS imtahanı &uuml;&ccedil;&uuml;n qeydiyyat:</strong><br />\r\n<a href=\"http://bit.ly/1Z9LPW4\" target=\"_blank\">http://bit.ly/1Z9LPW4</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>&Ouml;dənişi online etmək &uuml;&ccedil;&uuml;n n&ouml;vbəti linkdən istifadə edə bilərsiniz:</strong><br />\r\n<a href=\"http://barattson.com/payment/payonline/create\" target=\"_blank\">http://barattson.com/payment/payonline/create</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">IELTS testi barədə ətraflı məlumat &uuml;&ccedil;&uuml;n aşağıdakı ke&ccedil;iddən istifadə edin:<br />\r\n<a href=\"http://ielts.barattson.com/Home/Events/1\" target=\"_blank\">http://ielts.barattson.com/Home/Events/1</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bəxtiyar Vahabzadə k&uuml;&ccedil;. 5 AZ 1141, Bakı Azərbaycan (Elmlər Akademiyası m/st 2-ci &ccedil;ıxışı 3-c&uuml; mərtəbə)</span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Tel: +99412 5054497<br />\r\nMob: +99470 2054493<br />\r\nielts@barattson.com</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\"><a href=\"http://ielts.barattson.com/\" target=\"_blank\"><span style=\"color:rgb(255, 0, 0)\">http://ielts.barattson.com/</span></a></span></strong></p>\r\n','/uploads/events/5d90dace8ff59.jpg','2019-08-28','','350','','',1),(14,13,'ru','Официальные даты экзаменов IELTS','<p><span style=\"font-size:14px\">IDP Barattson: лицензированный тестовый центр AZ018, аккредитованный IELTS Australia.</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Для регистрации на экзамен IELTS:</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://bit.ly/1Z9LPW4\" target=\"_blank\">http://bit.ly/1Z9LPW4</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Для онлайн-платежей:</strong></span><br />\r\n<span style=\"font-size:14px\"><a href=\"http://barattson.com/payment/payonline/create\" target=\"_blank\">http://barattson.com/payment/payonline/create</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Для получения дополнительной информации об экзамене IELTS:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"http://ielts.barattson.com/Home/Events/1\" target=\"_blank\">http://ielts.barattson.com/Home/Events/1</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Адрес:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Бахтияр Вахабзаде, 5, 3-й этаж, Баку AZ1141, Азербайджан.&nbsp;(второй выход Элмляр Академиясы&nbsp;м/с)</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Контакты:</span></strong></p>\r\n\r\n<p>Тел:<span style=\"font-size:14px\">&nbsp;+99412 5054497</span><br />\r\nМоб<span style=\"font-size:14px\">: +99470 2054493<br />\r\nielts@barattson.com</span><br />\r\n<strong><span style=\"font-size:14px\"><span style=\"color:rgb(255, 0, 0)\"><a href=\"http://ielts.barattson.com/\" target=\"_blank\">http://ielts.barattson.com/</a>&nbsp;</span></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n','/uploads/events/5d90dace8ff59.jpg','2019-08-28','','350','','',1),(15,13,'en','Official IELTS Test Dates','<p><span style=\"font-size:14px\">Barattson IDP: AZ018 licensed test center accredited by IELTS Australia.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>For registering from IELTS exam:</strong><br />\r\nhttp://bit.ly/1Z9LPW4</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>For online payment:</strong><br />\r\nhttp://barattson.com/payment/payonline/create</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">For more information about IELTS Exam</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">http://ielts.barattson.com/Home/Events/1</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Adress</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Bakhtiyar Vahabzadeh, 5, 3rd floor, Baku AZ1141, Azerbaijan.(Elmler Akademiyasi m/st&nbsp; second exit)</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Contacts</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Tel:&nbsp; 99412 5054497<br />\r\nMob:&nbsp; 99470 2054493<br />\r\nielts@barattson.com<br />\r\n<a href=\"http://ielts.barattson.com/\">http://ielts.barattson.com/</a></span></p>\r\n\r\n<p>&nbsp;</p>\r\n','/uploads/events/5d90dace8ff59.jpg','2019-08-28','','350','','',1),(16,16,'az','Barattson – Rəsmi IELTS Test Mərkəzi!','<p><span style=\"font-size:14px\"><strong>Haqqında</strong><br />\r\n<br />\r\nBarattson ilk yarandığı g&uuml;ndən Azərbaycanda beynəlxalq təhsil standartlarını tətbiq etməyi və qısa zamanda Azərbaycan bazarında lider olmağı, habelə bir sıra beynəlxalq imtahanların rəsmi mərkəzi olmaq hədəfini qarşısına məqsəd qoymuşdur. Beynəlxalq imtahanlardan ən s&uuml;rətli inkişaf edən, ən &ccedil;ox rəğbət qazanan və tələbat duyulan&nbsp;məhz IELTS imtahanıdır. IELTS &uuml;&ccedil; partnyor təşkilat tərəfindən idarə olunur: Cambridge English Language Assessment (Cambridge ESOL), The British Council və IDP: IELTS Australia. Cambridge ESOL test m&uuml;əlliflərinin beynəlxalq tərtibat&ccedil;ılar komandası IELTS məzmununu hazırlayır və inkişaf etdirirlər, British Council və IDP: IELTS Australia testlərin təşkili, idarəolunması və nəzarəti, qiymətləndirilməsini və şəffaflığını təmin edirlər.<br />\r\nDil proqramlarını tədris edən təlim mərkəzi kimi, Barattson IELTS qurumlarına m&uuml;raciət edib və IDP Australia tərəfindən AZ018 n&ouml;mrəsi ilə akkreditə olunmuş, lisenziyalı Test mərkəzi adına layiq g&ouml;r&uuml;l&uuml;b.<br />\r\n<br />\r\nIELTS (International English Language Testing System) &uuml;nsiyyət dili ingilis dili olan bir &ccedil;ox &ouml;lkədə təhsil almaq və ya &ccedil;alışmaq istəyən namizədlərin dil bacarıqlarını &ouml;l&ccedil;mək &uuml;&ccedil;&uuml;n tərtib edilmişdir.<br />\r\n<br />\r\nIELTS dil bacarıqlarının qiymətləndirilməsinin ən y&uuml;ksək beynəlxalq standartıdır. Dil biliklərinin d&ouml;rd aspektini - dinləmə, oxu, yazı və danışıq bacarıqlarını 9 ballıq qiymətləndirmə sisteminə əsasən &ouml;l&ccedil;ən ilk test olmuşdur.<br />\r\n<br />\r\nIELTS d&uuml;nya &uuml;zrə 140-dan &ccedil;ox &ouml;lkədə, x&uuml;susən Avstraliya, Kanada, Yeni Zelandiya, Birləşmiş Krallıq və ABŞ-da fəaliyyət g&ouml;stərən universitetlər və işə g&ouml;t&uuml;rənlər tərəfindən tanınır. Həm&ccedil;inin IELTS 3000-dən artıq peşəkar qurumlar, immiqrasiya və digər d&ouml;vlət agentlikləri tərəfindən tanınır və qəbul olunur. Rəsmi dili ingilis dili olmayan, lakin tədris dili ingiliscə aparılan bir &ccedil;ox &ouml;lkənin təhsil ocaqlarında dil səviyyəsini isbatlayan rəsmi sənəd kimi IELTS sertifikatı tələb olunur.<br />\r\n<br />\r\nIELTS iki formatda m&ouml;vcuddur &ndash; Academic və General Training. Qeydiyyatdan ke&ccedil;ərkən iki formatdan birini se&ccedil;mək imkanı veriləcəkdir. M&uuml;vafiq modul &uuml;zrə imtahan verən şəxslər eyni dinləmə və danışıq, lakin fərqli yazı və oxu testlərini yerinə yetirirlər.<br />\r\n<br />\r\nIELTS Academic və IELTS General Training modullarını fərqləndirən x&uuml;susiyyət imtahanın hansı məqsədlə verilməsidir.<br />\r\nIELTS Academic tədrisin ingilis dilində həyata ke&ccedil;irildiyi bir &ccedil;ox ali təhsil m&uuml;əssisələrinə qəbul olunmaq &uuml;&ccedil;&uuml;n qoyulan tələblərdən biri kimi qəbul olunmuşdur.<br />\r\n<br />\r\nIELTS General Training Avstraliya, Kanada, Yeni Zelandiya, B&ouml;y&uuml;k Britaniya kimi ingilis dilli &ouml;lkəyə miqrasiya edib yaşamaq, orta təhsilini tamamlamaq və ya təlim proqramında iştirak etmək məqsədilə ingilis dilli &ouml;lkəyə səfər etməyə hazırlaşan şəxslər &uuml;&ccedil;&uuml;n m&uuml;nasib sayılır.<br />\r\n<br />\r\nİngilis dili &uuml;zrə m&uuml;təxəssislər qrupu tərəfindən tərtib edilmiş və inkişaf etdirilmiş IELTS imtahanı dil biliklərini ədalətli və d&uuml;r&uuml;st şəkildə m&uuml;əyyən edir.&nbsp;<br />\r\n<br />\r\nIELTS imtahanından qeydiyyatdan ke&ccedil;mək &uuml;&ccedil;&uuml;n aşağıdakı linkdən istifadə edin:<br />\r\n<br />\r\n<strong><a href=\"http://ielts.barattson.com/\"><span style=\"color:#FF0000\">http://ielts.barattson.com/</span></a></strong><br />\r\n<br />\r\nAlternativ olaraq bizimlə aşağıdakı vasitələrlə əlaqə saxlayın:<br />\r\n&Uuml;nvan: Bəxtiyar Vahabzadə k&uuml;&ccedil; 5, AZ 1141 Bakı, Azərbaycan (Elmlər Akademiyası m/st 2-ci &ccedil;ıxışı 3-c&uuml;&nbsp;mərtəbə.<br />\r\n+994 12 505 44 97<br />\r\n+994 70 205 44 93<br />\r\n<a href=\"#\">ielts@barattson.com</a></span></p>\r\n','/uploads/events/5d667dc231eee.jpg','2019-08-27','','','','',1),(17,16,'ru','Barattson - Официальный теcтовый центр IELTS!','<p>IELTS - самый популярный в мире тест по английскому языку. Ежегодно проводится более 3&nbsp;миллионов тестов IELTS.</p>\r\n\r\n<p><strong>IELTS управляется 3 организациями:</strong></p>\r\n\r\n<p>&nbsp; 1. Кембридж Оценка английского языка</p>\r\n\r\n<p>&nbsp; 2. Британский Совет</p>\r\n\r\n<p>&nbsp; 3.IDP Образование Австралии</p>\r\n\r\n<p>Принятие IELTS открывает двери - оно может помочь вам жить, учиться и работать по всему миру. IELTS был принят в качестве стандартной международной системы тестирования на знание английского языка целым рядом учреждений. Более 10 000 организаций по всему миру, более 3000 учреждений принимают IELTS, включая государственные органы, работодателей и академические учреждения. К ним относятся большинство всех учебных заведений, работающих на английском языке в Австралии, Канаде, Великобритании и Новой Зеландии. Образовательные учреждения США также начинают использовать его. Многие международные иммиграционные службы также используют IELTS, а также различные профессиональные организации, в том числе Британский и Австралийский медицинские советы и Министерство обороны Великобритании.</p>\r\n\r\n<p><strong>Запрос по результатам</strong></p>\r\n\r\n<p>Вы можете подать заявку на замечание (называемое &laquo;Запрос по результатам&raquo;) в своем центре тестирования в течение шести недель с даты проведения теста. Вы должны заполнить форму запроса о результатах и ​​заплатить сбор за запрос (160 австралийских долларов), который полностью возвращается в случае изменения балла вашей группы.</p>\r\n\r\n<p><strong>Важные моменты</strong></p>\r\n\r\n<p>После повторной оценки жалоба не принимается, решение является окончательным.<br />\r\nВы должны отправить оригинал TRF в центр тестирования, от которого вы его получили.<br />\r\nВ течение периода перемаркировки результаты вашего теста не могут быть использованы для подачи заявления в университет или для отправки в иммиграционную службу.<br />\r\nВ случае изменения баллов после запроса результатов мы выпустим новую форму отчета об испытаниях для кандидата, который может подать заявку на (5) пять дополнительных копий, которые будут бесплатно отправлены в учреждения-получатели в течение одного месяца с даты печати на нижняя часть новой формы отчета об испытаниях.<br />\r\nДля тех, чьи оценки остаются прежними, мы вернем оригинальную форму отчета об испытаниях кандидату, и возмещение не будет.<br />\r\nТест IELTS предназначен для того, чтобы помочь вам использовать английский в вашей новой жизни за границей. Менее чем за три часа, он оценит все ваши навыки английского: аудирование, чтение, письмо и разговорная речь.</p>\r\n\r\n<p>Самый эффективный способ проверить свои навыки разговорного английского - это очное общение. IELTS - это наиболее распространенный тест по английскому языку с индивидуальным тестом для оценки ваших навыков общения на английском языке.</p>\r\n\r\n<p><strong>Есть 2 версии теста IELTS: академический модуль (Academic)&nbsp; и модуль общего обучения&nbsp;(General).</strong></p>\r\n\r\n<p>IELTS Academic предназначен для людей, планирующих учиться в высших учебных заведениях, или для тех, кто ищет профессиональную регистрацию. Он оценивает, готовы ли вы начать учебу или обучение в среде, где используется английский язык.</p>\r\n\r\n<p><strong>Вы можете сдать IELTS Academic, если хотите:</strong></p>\r\n\r\n<p>&nbsp; 1. Учиться или обучаться в университете или колледже в качестве студента.</p>\r\n\r\n<p>&nbsp; 2. Учиться или обучаться в университете или колледже в качестве аспиранта.</p>\r\n\r\n<p>&nbsp; 3. Присоединитесь к профессиональной ассоциации.</p>\r\n\r\n<p>&nbsp; 4. Получите регистрацию в профессиональной организации (например, медицинская, сестринская, бухгалтерская, инженерная).</p>\r\n\r\n<p>Общее обучение измеряет уровень владения английским языком в практическом, повседневном контексте.</p>\r\n\r\n<p><strong>Вы можете сдать&nbsp;IELTS General Training, если хотите:</strong></p>\r\n\r\n<p>&nbsp; 1. Учиться или обучаться в средней школе.</p>\r\n\r\n<p>&nbsp; 2. Учиться или обучаться в колледже или учебном центре ниже уровня степени</p>\r\n\r\n<p>&nbsp; 3. Переехать в англоязычную страну.</p>\r\n\r\n<p><strong>Адрес:</strong></p>\r\n\r\n<p>Бахтияр Вахабзаде, 5, 3-й этаж, Баку AZ1141, Азербайджан.&nbsp;(второй выход Элмляр Академиясы&nbsp;м/с)</p>\r\n\r\n<p><strong>Контакты:</strong></p>\r\n\r\n<p>Тел:&nbsp;+99412 5054497<br />\r\nМоб: +99470 2054493<br />\r\nielts@barattson.com<br />\r\n<span style=\"color:rgb(255, 0, 0)\"><a href=\"http://ielts.barattson.com/\" style=\"box-sizing: border-box; color: rgb(0, 123, 255); text-decoration-line: none; background-color: transparent;\" target=\"_blank\">http://ielts.barattson.com/</a>&nbsp;</span></p>\r\n','/uploads/events/5d667dc231eee.jpg','2019-08-27','','','','',1),(18,16,'en','Barattson – Official IELTS Test Center!','<p><span style=\"font-size:14px\">IELTS is the world&rsquo;s most popular English language test. More than 3 million IELTS tests are taken each year.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">IELTS is administered by 3 organisations:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;1.Cambridge English Language Assessment</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;2.The British Council</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;3.IDP Education Australia</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Taking IELTS opens doors &ndash; it can help you live, study and work around the world. IELTS has been accepted as the standard international test system for English language proficiency by a whole range of institutions. More than 10,000 organisations worldwide, over 3,000 institutions accept IELTS, including government bodies, employers, and academic institutions. These include the majority of all education establishments operating in English in Australia, Canada, the UK and New Zealand. US educational institutions are also now starting to use it. Many international immigration services also use the IELTS as well as various professional organisations including the British and Australian Medical Councils and the UK Ministry of Defence.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Enquiry on Results</strong><span style=\"font-family:asap,sans-serif\">&nbsp;</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">You can apply for a remark (called an Enquiry on Results) at your test centre within six weeks of the test date. You must complete an&nbsp;<strong>Enquiry on Results Form</strong>&nbsp;and pay an enquiry fee (160 AUD), which is fully refunded if your band score changes.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:14px\"><strong>Important points</strong><span style=\"font-family:asap,sans-serif\">&nbsp;</span></span><br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">There is no appeal after this re-mark, the decision is final.</span></li>\r\n	<li><span style=\"font-size:14px\">You have to send the original TRF to the test centre you received it from.</span></li>\r\n	<li><span style=\"font-size:14px\">During the period of remarking your test results cannot be used to apply for a university or to be sent to an immigration office.</span></li>\r\n	<li><span style=\"font-size:14px\">Should the scores change after the Enquiry on Results, we will issue a new Test Report Form for the candidate, who can apply for (5) five extra copies to be sent to receiving institutions free of charge within one month of the printing date at the bottom of the new Test Report Form.</span></li>\r\n	<li><span style=\"font-size:14px\">For those whose scores remain the same we will return the original Test Report Form to the candidate and there will be no refund.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\">The IELTS test is designed to help you use English in your new life abroad. In less than three hours, it will assess all of your English skills: listening, reading, writing and speaking.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">The most effective way of testing your English conversation skills is face-to-face. IELTS is the most widely accepted English language test with a one-on-one speaking test to assess your English language communication skills.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">There are 2 versions of the IELTS test:the<strong>&nbsp;Academic Module</strong>&nbsp;and the&nbsp;<strong>General Training Module</strong>&nbsp;.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>IELTS Academic</strong>&nbsp;is designed for people planning to study in higher education or those seeking professional registration. It assesses whether you are ready to begin studying or training in an environment where English is the language used.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">You may take IELTS Academic if you want to:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;1.Study or train at a university or college as an undergraduate.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;2.Study or train at a university or college as a postgraduate.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;3.Join a professional association.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;4.Gain registration in a professional organisation (e.g. medical, nursing, accounting, engineering).</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>General Training</strong>&nbsp;measures English language proficiency in a practical, everyday context.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">You may take IELTS General Training if you want to:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;1.Study or train in secondary education.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;2.Study or train at a college or training centre below degree level</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;3.Migrate to an English speaking country.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Adress:</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Bakhtiyar Vahabzadeh, 5, 3rd floor, Baku AZ1141, Azerbaijan.(Elmler Akademiyasi m/st &nbsp;2nd exit)</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Contacts:</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">+994 12 505 44 97<br />\r\n+994 70 205 44 93<br />\r\n<a href=\"#\">ielts@barattson.com</a></span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\"><a href=\"http://ielts.barattson.com/\"><span style=\"color:rgb(255, 0, 0)\">http://ielts.barattson.com/</span></a></span></strong></p>\r\n','/uploads/events/5d667dc231eee.jpg','2019-08-27','','','','',1);
