INSERT INTO `slider` VALUES (4,4,'az','Barattson - Rəsmi IELTS Imtahan mərkəzidir','/uploads/slider/5d971c4ec217e.jpg','IELTS imtahanları qapıları açır - bu sizə dünyada yaşamağa, təhsil almağa və işləməyə kömək edə bilər.\r\n','Daha ətraflı','/events/16',1,3),(5,4,'ru','Barattson - Официальный теcтовый центр IELTS!','/uploads/slider/5d971c4ec217e.jpg','Barattson - Официальный теcтовый центр IELTS!','Подробнее','/events/16',1,3),(6,4,'en','Barattson - Official Paper and Computer Based IELTS Centre.','/uploads/slider/5d971c4ec217e.jpg','Taking IELTS opens doors - it can help you live, study and work around the world.\r\n','Read more','/events/16',1,3),(7,7,'az','Barattson 5-ci dəfə BMTK-da ingilis dili imtahanı keçirdi','/uploads/slider/5d3dbfea9898b.jpg','Barattson 5-ci dəfə BMTK-da ingilis dili imtahanı keçirdi','Daha ətraflı','http://barattson.orkhanalyshov.com/az/photo/barattson-5ci-defe-baku-modern-tehsil-kompleksinde-ingilis-dili-imtahani-kecirdi-7',1,4),(8,7,'ru','В пятый раз Barattson провел экзамен по английскому языку в БСОК.','/uploads/slider/5d3dbfea9898b.jpg','В пятый раз Barattson провел экзамен по английскому языку в БСОК.','Подробнее','http://barattson.orkhanalyshov.com/az/photo/barattson-5ci-defe-baku-modern-tehsil-kompleksinde-ingilis-dili-imtahani-kecirdi-7',1,4),(9,7,'en','For the fifth time, Barattson conducted an English exam at BMEC.','/uploads/slider/5d3dbfea9898b.jpg','For the fifth time, Barattson conducted an English exam at BMEC.','Read more','http://barattson.orkhanalyshov.com/az/photo/barattson-5ci-defe-baku-modern-tehsil-kompleksinde-ingilis-dili-imtahani-kecirdi-7',1,4),(16,16,'az','Elşən Rəhimov istefa verib','/uploads/slider/5d64e45fa7409.jpg','Elşən Rəhimov istefa verib','Daha ətraflı','/news-ads/13',1,1),(17,16,'ru','Эльшан Рахимов подал в отставку','/uploads/slider/5d64e45fa7409.jpg','Эльшан Рахимов подал в отставку','Подробнее','/news-ads/13',1,1),(18,16,'en','Elshan Rahimov has resigned  from position of CEO at Barattson!','/uploads/slider/5d64e45fa7409.jpg','Elshan Rahimov has resigned  from position of CEO at Barattson!','Read more','news-ads/13',1,1),(19,19,'az','Barattson PMS üzrə Açıq Qapı tədbiri təşkil edib','/uploads/slider/5d9072ebdd75f.jpg','Barattson PMS üzrə Açıq Qapı tədbiri təşkil edib','Daha ətraflı','http://barattson.orkhanalyshov.com/news-ads/16',1,NULL),(20,19,'ru','Barattson организовал мероприятие СПБ','/uploads/slider/5d9072ebdd75f.jpg','Barattson организовал мероприятие СПБ','Подробнее','http://barattson.orkhanalyshov.com/news-ads/16',1,NULL),(21,19,'en','Barattson organized an Open Door PAC event','/uploads/slider/5d9072ebdd75f.jpg','Barattson organized an Open Door PAC event','Read more','http://barattson.orkhanalyshov.com/news-ads/16',1,NULL);
