DROP TABLE IF EXISTS `static_pages`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `static_pages` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `r_id` int(11) DEFAULT NULL,
  `lang` char(4) DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL,
  `fact_1_title` varchar(255) DEFAULT NULL,
  `fact_1_body` text,
  `fact_2_title` varchar(255) DEFAULT NULL,
  `fact_2_body` text,
  `fact_3_title` varchar(255) DEFAULT NULL,
  `fact_3_body` text,
  `image` varchar(255) DEFAULT NULL,
  `mission` text,
  `vision` text,
  `value` text,
  `director_fullname` varchar(255) DEFAULT NULL,
  `director_image` varchar(255) DEFAULT NULL,
  `director_about` text,
  `director_message` text,
  `text` text,
  `seo_title` varchar(255) DEFAULT NULL,
  `seo_description` text,
  `seo_keywords` varchar(255) DEFAULT NULL,
  `seo_image` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

